
public class TypeCasting1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//widening method
		byte a = 10;
		int b = a;
		System.out.println(b);
		int r = 2;
		long d = r;
		System.out.println(d);
		//narrowing method 
		double t = 10.01;
		long h = (long)  t;
		System.out.println(t);
		int q = 20;
		long c = q;
		System.out.println(q);
		
		

	}

}
